import React from "react";
import Lottie from "react-lottie";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import animationData from "../animations/landinganimation/data";
import { Grid, Button, Typography } from "@material-ui/core";
import { useMediaQuery } from "@material-ui/core";
import ButtonArrow from "./ui/ButtonArrow";

import CustomSoftwareIcon from "./icons/CustomSoftwareIcon";
import MobileIcon from "./icons/MobileIcon";

const useStyles = makeStyles((theme) => ({
  animation: {
    maxWidth: "50em",
    minWidth: "21em",
    marginTop: "2em",
    marginLeft: "10%",
    [theme.breakpoints.down("sm")]: {
      maxWidth: "30em",
    },
  },
  estimateButton: {
    ...theme.typography.estimate,
    backgroundColor: theme.palette.common.black,
    borderRadius: 50,
    height: 45,
    width: 145,
    marginRight: 40,
    "&:hover": {
      backgroundColor: theme.palette.secondary.light,
    },
  },
  buttonContainer: {
    marginTop: "1em",
  },
  learnButtonHero: {
    ...theme.typography.learnButton,
    fontSize: "0.9rem",
    height: 45,
    width: 145,
  },
  learnButton: {
    ...theme.typography.learnButton,
    fontSize: "0.7rem",
    height: 35,
    padding: 5,
    [theme.breakpoints.down("sm")]: {
      marginBottom: "2em",
    },
  },
  mainConatiner: {
    marginTop: "5em",
    [theme.breakpoints.down("md")]: {
      marginTop: "3em",
    },
    [theme.breakpoints.down("xs")]: {
      marginTop: "2em",
    },
  },
  heroTextContainer: {
    minWidth: "21.5em",
    marginLeft: "1em",
    [theme.breakpoints.down("xs")]: {
      marginLeft: 0,
    },
  },
  specialText: {
    fontFamily: "Pacifico",
    color: theme.palette.common.black,
  },
  subtitle: {
    marginBottom: "1em",
  },
  icon: {
    marginLeft: "2em",
    [theme.breakpoints.down("xs")]: {
      marginLeft: 0,
    },
  },
  servicesContainer: {
    marginTop: "12em",
    [theme.breakpoints.down("sm")]: {
      padding: 25,
    },
  },
}));

export default function LandingPage() {
  const classes = useStyles();
  const theme = useTheme();
  const matchesSm = useMediaQuery(theme.breakpoints.down("sm"));

  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  return (
    <>
      <Grid container direction="column" className={classes.mainConatiner}>
        {/* Hero Block */}
        <Grid item>
          <Grid
            container
            justify="flex-end"
            alignItems="center"
            direction="row"
          >
            <Grid sm item className={classes.heroTextContainer}>
              <Typography align="center" variant="h2">
                Bringing High End Technology <br /> to all kind of business
              </Typography>
              <Grid
                container
                justify="center"
                className={classes.buttonContainer}
              >
                <Grid item>
                  <Button
                    className={classes.estimateButton}
                    variant="contained"
                  >
                    Free Estimate
                  </Button>
                </Grid>
                <Grid item>
                  <Button
                    variant="outlined"
                    className={classes.learnButtonHero}
                  >
                    <span style={{ marginRight: 10 }}>Learn More</span>
                    <ButtonArrow
                      width={15}
                      height={15}
                      fill={theme.palette.common.red}
                    />
                  </Button>
                </Grid>
              </Grid>
            </Grid>
            <Grid sm item className={classes.animation}>
              <Lottie options={defaultOptions} height={"100%"} width={"100%"} />
            </Grid>
          </Grid>
        </Grid>
        {/* Custom Software Block */}
        <Grid item>
          <Grid
            container
            direction="row"
            justify={matchesSm ? "center" : undefined}
            className={classes.servicesContainer}
          >
            <Grid
              item
              style={{
                marginLeft: matchesSm ? 0 : "5em",
                textAlign: matchesSm ? "center" : undefined,
              }}
            >
              <Typography variant="h4">Custom Software Development</Typography>
              <Typography variant="subtitle1" className={classes.subtitle}>
                Save Energy. Save Time. Save Money.{" "}
              </Typography>
              <Typography variant="subtitle1">
                Complete digital solutions, from investigation to{" "}
                <span className={classes.specialText}>celebration</span>
              </Typography>
              <Button variant="outlined" className={classes.learnButton}>
                <span style={{ marginRight: 10 }}>Learn More</span>
                <ButtonArrow
                  width={15}
                  height={15}
                  fill={theme.palette.common.red}
                />
              </Button>
            </Grid>

            <Grid item>
              <CustomSoftwareIcon
                className={classes.icon}
                main={theme.palette.primary.main}
                light={theme.palette.primary.light}
                dark={theme.palette.primary.dark}
              />
            </Grid>
          </Grid>
        </Grid>

        {/* iOs / Android App Development*/}
        <Grid item>
          <Grid
            container
            direction="row"
            justify={matchesSm ? "center" : "flex-end"}
            className={classes.servicesContainer}
          >
            <Grid
              item
              style={{
                marginLeft: matchesSm ? 0 : "5em",
                textAlign: matchesSm ? "center" : undefined,
              }}
            >
              <Typography variant="h4">iOS/Andriod App Development</Typography>
              <Typography variant="subtitle1" className={classes.subtitle}>
                Extend Functionality. Extend Access. Increase Engagement.{" "}
              </Typography>
              <Typography variant="subtitle1">
                Integrate your web experience or create a standalone
                app{matchesSm ? null : <br/>} with either mobile platform

              </Typography>
              <Button variant="outlined" className={classes.learnButton}>
                <span style={{ marginRight: 10 }}>Learn More</span>
                <ButtonArrow
                  width={15}
                  height={15}
                  fill={theme.palette.common.red}
                />
              </Button>
            </Grid>

            <Grid item>
              <Mo
                className={classes.icon}
                main={theme.palette.primary.main}
                light={theme.palette.primary.light}
                dark={theme.palette.primary.dark}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </>
  );
}
